import React, { Component } from 'react';
import Button from 'react-bootstrap/Button';

class ScheduleList extends Component {

    state = {
        generateNewSchedule: false
    }

    createNewSchedule = () => {
        this.setState({ generateNewSchedule: !this.state.generateNewSchedule })
    }

    render() {
        return (
            <div>
                <Button variant='primary' className='new-schedule-button' onClick={this.createNewSchedule}>New Schedule</Button>
                {
                    this.state.generateNewSchedule?
                    <div className='schedule-box'>
                        <h1>new schedule</h1>
                    </div>
                    :
                    <div></div>
                }
            </div>
        );
        this.setState({generateNewSchedule: false})
    }
}

export default ScheduleList;
